func threeSum(nums []int) [][]int {
	if isAllZeros(nums) && len(nums) > 3 {
		return [][]int{{0, 0, 0}}
	}
	sort.Sort(sort.IntSlice(nums))
	fmt.Println("Sorted Input", nums)
	output := [][]int{}
	allZeros := false
	for i, v := range nums {
		for j, w := range nums[i+1:] {
			for _, x := range nums[i+j+2:] {
				if v+w+x == 0 {
					fmt.Println("Entered If condition")
					if v == 0 && w == 0 && x == 0 {
						if !allZeros {
							output = append(output, []int{v, w, x})
							allZeros = true
						}
					}
					tempVal := []int{v, w, x}
					sort.Sort(sort.IntSlice(tempVal))
					if !isDuplicate(tempVal, output) {
						fmt.Println("Adding ")
						output = append(output, tempVal)
					}
				}
			}
		}
	}
	return output
}

// Returns true if it is a duplicate
// Else returns false if it is not a duplicate
func isDuplicate(current_val []int, values [][]int) bool {
	result := false
	for _, val_arr := range values {
		matched := true
		if !isAllZeros(val_arr) {
			for i, _ := range val_arr {
				if val_arr[i] == current_val[i] {
					return false
				} else {
					matched = false
					break
				}
			}
		}
		if matched {
			return true
		}
	}
	return result
}

// Method returns true if the array has all zeros
// else it returns false
func isAllZeros(val []int) bool {
	result := true
	for _, x := range val {
		if x != 0 {
			return false
		}
	}
	return result
}
