func threeSum(nums []int) [][]int {
	allZeros := false
	if isAllZeros(nums) && len(nums) > 3 {
		return [][]int{{0, 0, 0}}
	}
	output := [][]int{}
	for i, v := range nums {
		for j, w := range nums[i+1:] {
			for _, x := range nums[i+j+2:] {
				if v+w+x == 0 {
					if v == 0 && w == 0 && x == 0 {
						if !allZeros {
							output = append(output, []int{v, w, x})
							allZeros = true
						}
					}
					if !isDuplicate([]int{v, w, x}, output) {
						output = append(output, []int{v, w, x})
					}
				}
			}
		}
	}
	return output
}

// Returns true if it is a duplicate
// Else returns false if it is not a duplicate
func isDuplicate(current_val []int, values [][]int) bool {
	result := false
	if isAllZeros(current_val) {
		for _, val_arr := range values {
			if isAllZeros(val_arr) {
				return true
			}
		}
		return false
	} else {
		for _, val_arr := range values {
			if !isAllZeros(val_arr) {
				nested_match := 0
				for _, x := range val_arr {
					for _, y := range current_val {
						if x == y {
							nested_match++
							break
						}
					}
				}
				if nested_match == len(current_val) {
					return true
				}
			}

		}
	}
	return result
}

// Method returns true if the array has all zeros
// else it returns false
func isAllZeros(val []int) bool {
	result := true
	for _, x := range val {
		if x != 0 {
			return false
		}
	}
	return result
}
